def relative_to_assets(path: str) -> Path:
    return ASSETS_PATH / Path(path)

listOf_graphs = []
def read_graphs():
    jsonH = jh()
    graph_data = jsonH.read_json("DB2\graphs", "control_group_graphs", "Graphs")
    for g in graph_data:
        H = json_graph.adjacency_graph(g)
        listOf_graphs.append(H)


